import { buildUrl } from '@app/models';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { User } from '@app/models/user';

@Injectable({ providedIn: 'root' })
export class AuthenticationService {
  private currentUserSubject: BehaviorSubject<User | null>;
  public currentUserObservable: Observable<User | null>;

  constructor(private http: HttpClient) {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      this.currentUserSubject = new BehaviorSubject<User | null>(
        new User(JSON.parse(currentUser))
      );
      this.currentUserObservable = this.currentUserSubject.asObservable();
    }
  }

  public get currentUserValue(): User | null {
    return this.currentUserSubject.value;
  }

  login(email: string, password: string) {
    return this.http.post<any>(buildUrl('login'), { email, password }).pipe(
      map((res) => {
        // store user details and jwt token in local storage to keep user logged in between page refreshes
        const user: User = new User({
          ...res.data.user,
          token: res.data.token,
          expires_at: res.data.expires_at,
        });
        localStorage.setItem('currentUser', JSON.stringify(user));
        this.currentUserSubject.next(user);
        return user;
      })
    );
  }

  logout() {
    // remove user from local storage to log user out
    localStorage.removeItem('currentUser');
    this.currentUserSubject.next(null);
    location.reload();
  }

  register(
    email: string,
    username: string,
    password: string,
    password_confirmation: string
  ) {
    return this.http
      .post<any>(buildUrl('register'), {
        email,
        username,
        password,
        password_confirmation,
      })
      .pipe(
        map((res) => {
          const user: User = new User({
            ...res.data.user,
            token: res.data.token,
          });
          localStorage.setItem('currentUser', JSON.stringify(user));
          this.currentUserSubject.next(user);
        })
      );
  }

  forgot(email: string) {
    return this.http.post<any>(buildUrl('forgot-password'), {
      email,
    });
  }

  reset(
    token: string,
    email: string,
    password: string,
    password_confirmation: string
  ) {
    return this.http.post<any>(buildUrl('password-reset'), {
      token,
      email,
      password,
      password_confirmation,
    });
  }
}

import { LoaderService } from '@app/controls';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { APIResponse, buildUrl } from '@app/models';
import { BehaviorSubject } from 'rxjs';
import { Card } from '@app/pages';
import { Cache } from '@app/helpers';

@Injectable({ providedIn: 'root' })
export class CardService {
  constructor(private http: HttpClient, private loaderService: LoaderService) {}

  // Get card
  private getCardSubject = new BehaviorSubject<Card | null>(null);
  getCardObservable() {
    this.getCardSubject = new BehaviorSubject<Card | null>(null);
    return this.getCardSubject.asObservable();
  }
  getCard(code: string) {
    // Try cache
    if (Cache.card[code]) {
      this.getCardSubject.next(Cache.card[code]);
    } else {
      // Show loader
      this.loaderService.addItemLoading('getCard');
      // Request
      this.http.get<APIResponse>(buildUrl('card/' + code)).subscribe((res) => {
        const card = new Card(res.data);
        // Add to cache
        Cache.card[code] = card;
        this.getCardSubject.next(card);
        // Hide loader
        this.loaderService.clearItemLoading('getCard');
      });
    }
  }
}

