@import "settings", "scaffold/typography", "custom";

.card-detail {
	position: relative;
	overflow: hidden;
	background: linear-gradient(
		to right,
		darken(white, 20%),
		darken(mix(white, black, 80%), 20%)
	);
	&:after {
		content: "";
		box-shadow: $shadow-inset-large;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		position: absolute;
		z-index: 2;
		pointer-events: none;
	}
	.panel:after {
		@media (max-width: $screen-medium) {
			border-left: 0;
			border-right: 0;
		}
	}
	> .pokeball {
		position: absolute;
		left: 50%;
		top: 50%;
		transform: translate(-50%, -50%);
		width: 1000px;
		opacity: 0.1;
		z-index: 1;
		pointer-events: none;
		max-width: 120%;
	}
	@media (max-width: $screen-medium) {
		padding-left: 0;
		padding-right: 0;
		padding-bottom: 0;
	}
	@each $type, $color in $poke-types {
		&.#{$type} {
			background: linear-gradient(
				to right,
				darken($color, 20%),
				darken(mix($color, black, 80%), 20%)
			);
		}
	}
	.card-image-wrapper {
		z-index: 2;
		&.active {
			.img-card-image {
				opacity: 0;
			}
			.card-image-inner {
				&:after {
					display: none;
				}
			}
		}
		.img-card-image {
			max-width: 240px;
			cursor: zoom-in;
			display: block;
			opacity: 1;
			&:hover {
				box-shadow: $shadow-focus;
			}
			@media (max-height: $screen-small + 40px) {
				opacity: 1 !important;
				cursor: default;
			}
			@media (max-width: $screen-medium) {
				margin-left: auto;
				margin-right: auto;
			}
		}
	}
}
.card-energies {
	.card-energy {
		width: 24px;
		height: 24px;
		margin-top: 1px;
		border-radius: 50%;
		transition: $transition;
		cursor: pointer;
		position: relative;
		display: block;
		&:hover {
			box-shadow: $shadow-focus, $shadow-sheen;
			border-color: $primary-color;
		}
		.card-energy-inner {
			width: 24px;
			height: 24px;
			border-radius: 50%;
			box-shadow: $shadow-sheen;
		}
		@each $type, $color in $poke-types {
			&.#{$type} {
				background: $color;
			}
		}
		&:before {
			content: "";
			border-radius: 50%;
			width: 100%;
			height: 100%;
			box-shadow: $shadow-sheen;
			z-index: 10;
			position: absolute;
			border: $border;
			top: 0;
			left: 0;
		}
	}
	img {
		width: 24px;
		height: 24px;
		display: block;
		border-radius: 50%;
		box-shadow: $shadow;
	}
}
.artist-link {
	color: white;
