import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

import { Items } from './items';
import { ItemDisplayType } from './items-filter';
import { Title } from '@angular/platform-browser';
import { AppSettings } from '@app/app';
import { Empty } from '@app/controls';
import { IconProp } from '@fortawesome/fontawesome-svg-core';

@Component({
  selector: 'mb-items',
  templateUrl: 'items.component.html',
  styleUrls: ['./items.component.scss'],
})
export class ItemsComponent implements OnInit {
  @Input() items: Items;
  @Output() outputGetItems: EventEmitter<void> = new EventEmitter();
  @Output() outputDisplayModeChanged: EventEmitter<ItemDisplayType> =
    new EventEmitter();
  empty: Empty;

  constructor(private route: ActivatedRoute, private router: Router) {}

  ngOnInit() {
    // Get query params
    this.route.queryParams.subscribe((params) => {
      if (params['search']) {
        this.items.filter.textboxSearch.value = params['search'];
      }

      if (params['sortby']) {
        this.items.filter.selectSortBy.value = params['sortby'];
      }

      if (params['sortdirection']) {
        this.items.filter.selectSortDirection.value = params['sortdirection'];
      }

      if (params['displaymode']) {
        this.items.itemDisplayType = (ItemDisplayType as any)[
          params['displaymode']
        ];
        this.items.filter.setDisplayMode(this.items.itemDisplayType);
      }

      if (params['pagesize']) {
        this.items.footer.pageSize = +params['pagesize'];
      }

      if (params['page']) {
        this.items.footer.page = +params['page'];
      }
    });

    this.outputGetItems.emit();

    this.empty = new Empty({
      text: this.items.noResults,
      button: this.items.buttonNoResults,
      icon: this.items.noResultsIcon as IconProp,
      image: this.items.noResultsImage,
    });
  }

  updateQueryParams() {
    this.router.navigate([], {
      queryParams: {
        pagesize: this.items.footer.pageSize,
        page: this.items.footer.page,
        search: this.items.filter.textboxSearch.value,
        sortby: this.items.filter.selectSortBy.value,
        sortdirection: this.items.filter.selectSortDirection.value,
        displaymode: this.items.filter.displayMode.toLowerCase(),
      },
    });
  }

  showEmpty() {
    return this.items.itemGroups.length == 0;
  }

  showGroups() {
    return this.items.itemGroups.length > 0;
  }

  displayModeChanged(itemDisplayType: ItemDisplayType) {
    this.items.itemDisplayType = itemDisplayType;
    this.items.filter.displayMode = this.items.itemDisplayType;
    this.outputDisplayModeChanged.emit(itemDisplayType);
    this.updateQueryParams();
  }

  _outputGetItems() {
    this.outputGetItems.emit();
    this.updateQueryParams();
  }
}

import { Component, OnInit, Input, Output, EventEmitter, ViewEncapsulation } from '@angular/core';
import { ItemsFilter, ItemDisplayType } from './items-filter';
import { Menu, MenuItem } from '@app/controls/menu';
import { ActivatedRoute, Router } from '@angular/router';
import { Icons } from '@app/models/icons';

@Component({
	selector: 'mb-items-filter',
	templateUrl: 'items-filter.component.html',
	styleUrls: ['./items-filter.component.scss'],
	encapsulation: ViewEncapsulation.None
})

export class ItemsFilterComponent implements OnInit {

	@Input() itemsFilter: ItemsFilter;
	@Output() outputGetItems: EventEmitter<string> = new EventEmitter;
	@Output() outputDisplayModeChanged: EventEmitter<ItemDisplayType> = new EventEmitter;

	constructor(
		private route: ActivatedRoute,
		private router: Router) { }

	ngOnInit() {
		this.setupDefaultControls();
	}

	setupDefaultControls() {

		// Search
		this.itemsFilter.textboxSearch.keydownEnter = value => {
			this.outputGetItems.emit();
		}
		this.itemsFilter.textboxSearch.clickIcon = value => {
			this.outputGetItems.emit();
		}
		this.itemsFilter.textboxSearch.clickClear = () => {
			this.outputGetItems.emit();
		}

		// Sort by
		this.itemsFilter.selectSortBy.change = value => {
			this.outputGetItems.emit();
		}

		// Sort direction
		this.itemsFilter.selectSortDirection.change = value => {
			this.outputGetItems.emit();
		}

		// Display modes
		this.itemsFilter.menuDisplayMode = new Menu({
			clearActiveClickOutside: false,
			horizontal: true,
			classes: "round border shadow-light",
		});

		// Grid
		if (this.itemsFilter.showGridDisplayMode) {
			this.itemsFilter.menuItemGridDisplayMode = new MenuItem({
				text: "Grid",
				icon: Icons.grid,
				active: this.itemsFilter.displayMode == ItemDisplayType.grid,
				click: () => {
					this.setDisplayMode(ItemDisplayType.grid);
				}
			});
			this.itemsFilter.menuDisplayMode.items.push(this.itemsFilter.menuItemGridDisplayMode);
		}
		
		// List
		if (this.itemsFilter.showListDisplayMode) { 
			this.itemsFilter.menuItemListDisplayMode = new MenuItem({
				text: "List",
				icon: Icons.list,
				active: this.itemsFilter.displayMode == ItemDisplayType.list,
				click: () => {
					this.setDisplayMode(ItemDisplayType.list);
				}
			});
			this.itemsFilter.menuDisplayMode.items.push(this.itemsFilter.menuItemListDisplayMode);
		}
	}

	setDisplayMode(type: ItemDisplayType) {
		this.outputDisplayModeChanged.emit(type);
		this.itemsFilter.setDisplayMode(type);
		this.itemsFilter.displayMode = type;
	}
}

