export class UserCardComponent implements OnInit {
  @Input() item: UserCard;
  @Output() deleted: EventEmitter<boolean> = new EventEmitter();
  @Output() updated: EventEmitter<UserCard> = new EventEmitter();
  selectCondition: Select;
