  buildControls() {
    // Notes
    this.formNotes = this.formBuilder.group({
      notesControl: [''],
    });

    // Condition
    this.selectCondition = new Select({
      value: this.item.condition,
      classes: 'square-right',
      optionGroups: [
        new SelectOptionGroup({
          label: 'Condition',
        }),
        new SelectOptionGroup({
          label: 'Condition Graded',
        }),
      ],
      change: (value) => {
        this.updated.emit(
          new UserCard({
            ...this.item,
            condition: (Condition as any)[value],
          })
        );
      },
    });
    for (let condition in Condition) {
      this.selectCondition.optionGroups[0].options.push(
        new SelectOption({
          text: (Condition as any)[condition],
          value: (Condition as any)[condition],
          selected: (Condition as any)[condition] === this.item.condition,
        })
      );
    }
    for (let conditionGraded in ConditionGraded) {
      this.selectCondition.optionGroups[1].options.push(
        new SelectOption({
          text: (ConditionGraded as any)[conditionGraded],
          value: (ConditionGraded as any)[conditionGraded],
          selected:
            (ConditionGraded as any)[conditionGraded] === this.item.condition,
        })
      );
    }
    this.selectCondition.value = this.item.condition;

    // Grading company
    this.selectGradingCompany = new Select({
      classes: 'square',
      optionGroups: [
        new SelectOptionGroup({
          label: 'Graded By',
        }),
      ],
    });
    for (let gradingCompany in GradingCompany) {
      this.selectGradingCompany.optionGroups[0].options.push(
        new SelectOption({
          text: (GradingCompany as any)[gradingCompany],
          value: (GradingCompany as any)[gradingCompany],
        })
      );
    }

    // Printing
    this.selectPrinting = new Select({
      classes: 'square',
      optionGroups: [
        new SelectOptionGroup({
          label: 'Printing',
        }),
      ],
      change: (value) => {
        this.updated.emit(
          new UserCard({
            ...this.item,
            printing: (Printings as any)[value],
          })
        );
      },
    });
    for (let printVersion in Printings) {
      this.selectPrinting.optionGroups[0].options.push(
        new SelectOption({
          text: (Printings as any)[printVersion],
          value: (Printings as any)[printVersion],
        })
      );
    }

    // Notes
    this.buttonNotes = new Button({
      text: 'Notes',
      classes: 'square secondary',
      width: '100%',
      click: () => {
        this.dialogService.setDialog(
          new Dialog({
            title: 'Notes',
            form: new MBForm({
              formGroup: this.formNotes,
              groups: [
                new MBFormGroup({
                  controls: [
                    new MBFormControl({
                      //formControl: this.formNotes.controls.notesControl,
                      control: new Textarea({}),
                    }),
                  ],
                }),
              ],
            }),
          })
        );
      },
    });

    // Binder
    this.selectBinder = new Select({
      classes: 'square',
      multiple: true,
      options: [
        new SelectOption({
          text: 'Binder 1',
          value: 'binder1',
          selected: true,
        }),
      ],
    });

    // Button remove
    this.buttonRemove = new Button({
      icon: Icons.trash,
      classes: 'secondary square-left',
      click: () => {
        if (confirm('Are you sure you want to delete this item?')) {
          this.deleted.emit(true);
        }
      },
    });
  }
}

import { UserCardsService } from './user-cards.service';
import { Component, Input, OnInit } from '@angular/core';
import { Icons } from '@app/models';
import { Button, Checkbox, Empty } from '@app/controls';
import { UserCard } from './user-card';

@Component({
  selector: 'user-cards',
  templateUrl: './user-cards.component.html',
  styleUrls: ['./user-cards.component.scss'],
})
export class UserCardsComponent implements OnInit {
  @Input() card_id: number;
  @Input() userCards: UserCard[] = [];
  buttonAdd: Button;
  checkboxWishList: Checkbox;
  buttonViewAll: Button;
  empty: Empty;
  buttonNotes: Button;
  buttonDelete: Button;

  constructor(private userCardsService: UserCardsService) {}

  ngOnInit(): void {
    this.setupControls();
    this.setupSubscriptions();
  }

  setupControls() {
    this.empty = new Empty({
      text: 'This card is not in your collection',
      icon: Icons.box,
      button: new Button({
        text: 'Add to Collection',
        icon: Icons.plus,
        click: () => {
          this.addItem();
        },
      }),
    });

    // Button add
    this.buttonAdd = new Button({
      text: 'Add to Collection',
      icon: Icons.plus,
      classes: 'secondary',
      click: () => {
        this.addItem();
      },
    });

    // Button view all
    this.buttonViewAll = new Button({
      text: 'View Collection',
      icon: Icons.externalLink,
    });

    // Header buttons for spacing
    this.buttonNotes = new Button({
      text: 'Notes',
    });
    this.buttonDelete = new Button({
      icon: Icons.trash,
    });

    // Checkbox wish list
    this.checkboxWishList = new Checkbox({
      text: 'Wishlist',
    });
  }

  setupSubscriptions() {
    this.userCardsService.addUserCardObservable().subscribe((addedCard) => {
      if (addedCard) {
        this.userCards.push(addedCard);
      }
    });
  }

  addItem(
    userCard: UserCard = new UserCard({
      card_id: this.card_id,
    })
  ) {
    this.userCardsService.addUserCard(userCard);
  }

  deleteItem(userCard: UserCard) {
    this.userCardsService.removeUserCard(userCard.id).subscribe((res) => {
      if (res.success)
        this.userCards = this.userCards.filter(
          (_userCard) => _userCard.id !== userCard.id
        );
    });
  }

  updateItem(userCard: UserCard) {
    this.userCardsService.updateUserCard(userCard).subscribe((res) => {
      if (res.success)
        this.userCards = this.userCards.map((_userCard) =>
          _userCard.id === userCard.id ? userCard : _userCard
        );
    });
  }
}

